              type="number"
              id="outlined-basic"
              label="Enter age"
              margin="dense"
              fullWidth
              variant="filled"
            />
            <TextField
              name="hobbies"
              required
              onChange={handleChange}
              id="filled-multiline-flexible"
              label="Multiline hobbies"
              multiline
              fullWidth
              maxRows={4}
              variant="filled"
            />
            </form>
          </DialogContent>
          <DialogActions>
            <Button onClick={handleClose}>Cancel</Button>
            <Button type="submit">Update</Button>
          </DialogActions>
        </Dialog>

    </>
  );
};

